package com.yash.tms.model;

import java.util.Date;

import javax.persistence.Temporal;
import javax.persistence.TemporalType;

/**
 * this is a dummy model for adding neew resource front front end part
 * @author megha.dengre
 *
 */
public class ResourceEmployee {
	
	/**
	 * primary key of resource table(auto generated)
	 */
	private int resource_id;
	/**
	 * primary key of employee table (auto generated)
	 */
	private int employee_id;
	
	/**
	 * first name of resource 
	 */
	private String firstname;
	/**
	 * last name of resource
	 */
	private String lastname;
	/**
	 * role_id be used as id of role entity and it defines the role of the
	 * employee,it is foreign key.
	 */
	private int role_id;
	/**
	 * email_id is a yash email id and it is unique.
	 */
	private String email_id;
	/**
	 * batch_id be used as id of batch entity and it defines the batch of the
	 * employee,it is foreign key.
	 */
	private int batch_id;
	/**
	 * status_id be used as id of status entity and it defines the
	 * status(active/inactive) of the employee,it is foreign key.
	 */
	private int status_id;
	
	/**
	 * contribution time in hours for each day
	 */
	private double contribution_time;
	/**
	 * createdby is used to hold the id of employee who have created the record.
	 */
	private int createdby;
	/**
	 * modifiedby is used to hold the id of employee who have last modified the
	 * record.
	 */

	private int modifiedby;
	/**
	 * createddate is used to hold the date of creation of the employee entity
	 * record.
	 */
	
	private Date createddate;
	/**
	 * modifieddate is used to hold the date of last modification of the
	 * employee entity record.
	 */
	private Date modifieddate;
	
	/**
	 * name will be used as the role name of the Employee. ex. Trinee, Trainer
	 */
	private String role_name;
	
	/**
	 * this name will be used as the batch name ex. Jan2016
	 */
	private String batch_name;
	
	
	public String getRole_name() {
		return role_name;
	}
	public void setRole_name(String role_name) {
		this.role_name = role_name;
	}
	public String getBatch_name() {
		return batch_name;
	}
	public void setBatch_name(String batch_name) {
		this.batch_name = batch_name;
	}
	public int getResource_id() {
		return resource_id;
	}
	public void setResource_id(int resource_id) {
		this.resource_id = resource_id;
	}
	public int getEmployee_id() {
		return employee_id;
	}
	public void setEmployee_id(int employee_id) {
		this.employee_id = employee_id;
	}
	public String getFirstname() {
		return firstname;
	}
	public void setFirstname(String firstname) {
		this.firstname = firstname;
	}
	public String getLastname() {
		return lastname;
	}
	public void setLastname(String lastname) {
		this.lastname = lastname;
	}
	public int getRole_id() {
		return role_id;
	}
	public void setRole_id(int role_id) {
		this.role_id = role_id;
	}
	public String getEmail_id() {
		return email_id;
	}
	public void setEmail_id(String email_id) {
		this.email_id = email_id;
	}
	public int getBatch_id() {
		return batch_id;
	}
	public void setBatch_id(int batch_id) {
		this.batch_id = batch_id;
	}
	public int getStatus_id() {
		return status_id;
	}
	public void setStatus_id(int status_id) {
		this.status_id = status_id;
	}
	public double getContribution_time() {
		return contribution_time;
	}
	public void setContribution_time(double contribution_time) {
		this.contribution_time = contribution_time;
	}
	public int getCreatedby() {
		return createdby;
	}
	public void setCreatedby(int createdby) {
		this.createdby = createdby;
	}
	public int getModifiedby() {
		return modifiedby;
	}
	public void setModifiedby(int modifiedby) {
		this.modifiedby = modifiedby;
	}
	public Date getCreateddate() {
		return createddate;
	}
	public void setCreateddate(Date createddate) {
		this.createddate = createddate;
	}
	public Date getModifieddate() {
		return modifieddate;
	}
	public void setModifieddate(Date modifieddate) {
		this.modifieddate = modifieddate;
	}
	
}
